import java.util.Arrays;

public class question2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	   String [] str1= {"eat","tea","tan","ate","nat","bat"};
	   for(int i=0;i<str1.length;i++) {
		  
		   Arrays.sort(str1);
		  
		   System.out.println(str1[i]);
		   
		   
		   
	   }
		
		

	}

}

 









































